INSERT INTO `wp_comments` (`comment_ID`, `comment_post_ID`, `comment_author`, `comment_author_email`, `comment_author_url`, `comment_author_IP`, `comment_date`, `comment_date_gmt`, `comment_content`, `comment_karma`, `comment_approved`, `comment_agent`, `comment_type`, `comment_parent`, `user_id`) VALUES
(1, 1, 'A WordPress Commenter', 'wapuu@wordpress.example', 'https://wordpress.org/', '', '2017-06-29 05:54:43', '2017-06-29 05:54:43', 'Hi, this is a comment.\nTo get started with moderating, editing, and deleting comments, please visit the Comments screen in the dashboard.\nCommenter avatars come from <a href="https://gravatar.com">Gravatar</a>.', 0, '1', '', '', 0, 0),
(2, 1327, 'Marcos Eduardo', 'marcoseduardoss@gmail.com', 'http://erer', '200.129.22.52', '2017-02-03 16:32:27', '2017-02-03 16:32:27', 'ere', 0, '0', '', '', 0, 0),
(3, 1333, 'Manuel Vicedo', 'mvicedo@cpothemes.com', '', '46.26.170.71', '2013-02-18 11:11:40', '2013-02-18 11:11:40', 'Another user comment, this time by the author of the post. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.', 0, '1', '', '', 0, 1),
(4, 1333, 'Manuel Vicedo', 'mvicedo@cpo.es', '', '46.26.170.71', '2013-02-18 11:12:33', '2013-02-18 11:12:33', 'A nested comment, replying to another one. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.', 0, '1', '', '', 3, 1),
(5, 1333, 'Joseph Scott', 'joseph@randomnetworks.com', '', '46.26.170.71', '2013-02-18 11:17:53', '2013-02-18 11:17:53', 'This is a standard comment, complete with gravatar and permalink. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.', 0, '1', '', '', 0, 0),
(6, 1333, 'Alex Shiels', 'tellyworth@gmail.com', '', '46.26.170.71', '2013-02-18 11:18:54', '2013-02-18 11:18:54', 'Another comment for this post. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.', 0, '1', '', '', 0, 0),
(7, 1333, 'Waffe', 'wpcom@blogwaffe.com', '', '46.26.170.71', '2013-02-18 11:20:12', '2013-02-18 11:20:12', 'A reply to the author of the post. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.', 0, '1', '', '', 3, 0),
(8, 1333, 'CPO', 'info@cpo.es', 'http://www.cpo.es', '46.26.170.71', '2013-02-18 11:20:46', '2013-02-18 11:20:46', 'I really like this article, itâ€™s very easy to read. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.', 0, '1', '', '', 0, 0),
(9, 1335, 'tester', 'test@tester.com', 'http://tester.com', '144.48.0.12', '2016-12-08 16:21:17', '2016-12-08 16:21:17', 'testing', 0, '0', '', '', 0, 0),
(10, 1982, 'Alexsus', 'asd@mail.com', '', '93.72.90.52', '2016-12-29 19:41:05', '2016-12-29 19:41:05', 'dasd Sdkjn1!', 0, 'post-trashed', '', '', 0, 0),
(11, 1982, 'ad', 'asda@agra.com', '', '::1', '2017-07-03 11:37:21', '2017-07-03 11:37:21', 'aax', 0, 'post-trashed', 'Mozilla/5.0 (X11; Linux x86_64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/55.0.2883.87 Safari/537.36 OPR/42.0.2393.94', '', 0, 0);
