INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(1, 1, '2017-06-29 05:54:43', '2017-06-29 05:54:43', 'Welcome to WordPress. This is your first post. Edit or delete it, then start writing!', 'Hello world!', '', 'publish', 'open', 'open', '', 'hello-world', '', '', '2017-06-29 05:54:43', '2017-06-29 05:54:43', '', 0, 'http://localhost/wordpress/?p=1', 0, 'post', '', 1),
(2, 1, '2017-06-29 05:54:43', '2017-06-29 05:54:43', 'This is an example page. It\'s different from a blog post because it will stay in one place and will show up in your site navigation (in most themes). Most people start with an About page that introduces them to potential site visitors. It might say something like this:\n\n<blockquote>Hi there! I\'m a bike messenger by day, aspiring actor by night, and this is my website. I live in Los Angeles, have a great dog named Jack, and I like pi&#241;a coladas. (And gettin\' caught in the rain.)</blockquote>\n\n...or something like this:\n\n<blockquote>The XYZ Doohickey Company was founded in 1971, and has been providing quality doohickeys to the public ever since. Located in Gotham City, XYZ employs over 2,000 people and does all kinds of awesome things for the Gotham community.</blockquote>\n\nAs a new WordPress user, you should go to <a href="http://localhost/wordpress/wp-admin/">your dashboard</a> to delete this page and create new pages for your content. Have fun!', 'Sample Page', '', 'trash', 'closed', 'open', '', 'sample-page__trashed', '', '', '2017-07-05 10:23:14', '2017-07-05 10:23:14', '', 0, 'http://localhost/wordpress/?page_id=2', 0, 'page', '', 0),
(4, 1, '2017-06-29 06:06:04', '2017-06-29 06:06:04', '', 'Espresso', '', 'inherit', 'open', 'closed', '', 'espresso', '', '', '2017-06-29 06:06:04', '2017-06-29 06:06:04', '', 0, 'http://localhost/wordpress/wp-content/uploads/2017/06/espresso.jpg', 0, 'attachment', 'image/jpeg', 0),
(5, 1, '2017-06-29 06:06:04', '2017-06-29 06:06:04', '', 'Sandwich', '', 'inherit', 'open', 'closed', '', 'sandwich', '', '', '2017-06-29 06:06:04', '2017-06-29 06:06:04', '', 0, 'http://localhost/wordpress/wp-content/uploads/2017/06/sandwich.jpg', 0, 'attachment', 'image/jpeg', 0),
(6, 1, '2017-06-29 06:06:04', '2017-06-29 06:06:04', '', 'Coffee', '', 'inherit', 'open', 'closed', '', 'coffee', '', '', '2017-06-29 06:06:04', '2017-06-29 06:06:04', '', 0, 'http://localhost/wordpress/wp-content/uploads/2017/06/coffee.jpg', 0, 'attachment', 'image/jpeg', 0),
(7, 1, '2017-06-29 06:06:04', '2017-06-29 06:06:04', 'Welcome to your site! This is your homepage, which is what most visitors will see when they come to your site for the first time.', 'Home', '', 'trash', 'closed', 'closed', '', 'home__trashed', '', '', '2017-07-05 10:23:16', '2017-07-05 10:23:16', '', 728, 'http://localhost/wordpress/?page_id=7', 0, 'page', '', 0),
(8, 1, '2017-06-29 06:06:04', '2017-06-29 06:06:04', 'You might be an artist who would like to introduce yourself and your work here or maybe you&rsquo;re a business with a mission to describe.', 'About', '', 'trash', 'closed', 'closed', '', 'about__trashed', '', '', '2017-07-05 10:23:59', '2017-07-05 10:23:59', '', 0, 'http://localhost/wordpress/?page_id=8', 0, 'page', '', 0),
(9, 1, '2017-06-29 06:06:05', '2017-06-29 06:06:05', 'This is a page with some basic contact information, such as an address and phone number. You might also try a plugin to add a contact form.', 'Contact', '', 'trash', 'closed', 'closed', '', 'contact__trashed', '', '', '2017-07-05 10:22:16', '2017-07-05 10:22:16', '', 0, 'http://localhost/wordpress/?page_id=9', 0, 'page', '', 0),
